#!/usr/bin/env bash

set -euo pipefail

cat_pubkey() {
  cat <<EOF
ssh-rsa  countskm@digicat.org-2014
EOF
}

cat_bashrc() {
  cat <<EOF
if [[ \$- != *i* ]]; then return; fi

export EDITOR=vim
export HISTCONTROL=ignoreboth
export HISTFILESIZE=-1
export HISTSIZE=-1
export PS1='[\$(pwd)]$ '

shopt -s checkwinsize

source /etc/bash_completion
EOF
}

upos() {
  export DEBIAN_FRONTEND=noninteractive

  apt update

  apt-get -o "Dpkg::Options::=--force-confold" dist-upgrade -q -y --force-yes

  apt install -y tree git tmux

  cat <<EOF > /etc/ssh/sshd_config.d/50-cloud-init.conf
PasswordAuthentication yes
EOF

  service ssh restart
}

mkuser() {
  local user=$1; shift

  useradd -m -s /bin/bash ${user}

  if [[ $# -gt 0 ]]; then # set passwd
    echo "${user}:${1}" | chpasswd
  fi

  cat_bashrc > /home/${user}/.bashrc

  mkdir -p /home/${user}/.ssh

  cat_pubkey > /home/${user}/.ssh/authorized_keys

  chmod -R u=rX,go=-rwx /home/${user}/.ssh

  chown -R ${user}:${user} /home/${user}
}

#-----------------------------------------------------------------------------------------------------
# global vars
#-----------------------------------------------------------------------------------------------------

OS_PACKAGES="
  tree
  git
  tmux
"

#-----------------------------------------------------------------------------------------------------
# main
#-----------------------------------------------------------------------------------------------------

"$@"
